export default function ContactModal() {
  const [isOpen, setIsOpen] = useState(false);
  const [displayedAscii, setDisplayedAscii] = useState('');

  useEffect(() => {
    if (!isOpen) {
      setDisplayedAscii('');
      return;
    }
    
    let index = 0;
    const interval = setInterval(() => {
      setDisplayedAscii(ALLIGATOR_ASCII.slice(0, index + 1));
      index++;
      if (index > ALLIGATOR_ASCII.length) clearInterval(interval);
    }, 5);
    
    return () => clearInterval(interval);
  }, [isOpen]);

  return (
    <>
      <style>{glowingStyles}</style>
      {/* Button to open modal */}
      <button 
        onClick={() => setIsOpen(true)}
        className="glowing-btn text-center"
      >
        <span className="glowing-txt">
          INIT<span className="faulty-letter">IATE </span><span>SECURE</span> <span>HANDSHAKE</span>
        </span>
      </button>

      {/* Modal */}
      {isOpen && (
        <div 
          className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/95 backdrop-blur-md"
          onClick={(e) => e.target === e.currentTarget && setIsOpen(false)}
        >
          <div className="relative w-full max-w-3xl border border-[#00FF00]/40 bg-black overflow-hidden shadow-[0_0_50px_rgba(0,255,0,0.1)]">
                        
            <div className="relative z-10 bg-black/60 backdrop-blur-sm">
              <div className="bg-[#00FF00]/10 border-b border-[#00FF00]/30 px-4 py-2 flex justify-between items-center font-mono text-[10px] uppercase text-[#00FF00]">
                <span>Terminal: Secure_Handshake_v2.1</span>
                <span className="animate-pulse">Status: Handshaking...</span>
              </div>

              <div className="p-8 space-y-6">
                <div className="border-l-2 border-[#00FF00] pl-4">
                  <h3 className="text-white font-bold uppercase text-xs tracking-widest">Operator: Fran M. Vazquez</h3>
                  <div className="text-[#00FF00]/70 text-[10px] mt-1 space-y-1 font-mono uppercase">
                    <p> * Master in Ethical Hacking - Deloitte EMEA Cybersphere</p>
                    <p> * 10+ Years Tactical Leadership (Securitas S.A.) [cite: 4, 38]</p>
                    
                  </div>
                </div>

                <form className="space-y-4 font-mono relative z-20" onSubmit={(e) => { e.preventDefault(); alert('Message sent!'); setIsOpen(false); }}>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="text-[#00FF00]/70 text-[10px] uppercase block mb-2 tracking-widest"> ! SOURCE_ID (Email)</label>
                      <input type="email" placeholder="operator@cybersphere.com" className="w-full bg-black/80 border border-[#00FF00]/30 p-3 text-[#00FF00] text-sm focus:border-[#00FF00] outline-none" required />
                    </div>
                    <div>
                      <label className="text-[#00FF00]/70 text-[10px] uppercase block mb-2 tracking-widest"> ! HANDSHAKE_PRIORITY</label>
                      <select className="w-full bg-black/80 border border-[#00FF00]/30 p-3 text-[#00FF00] text-sm outline-none cursor-pointer">
                        <option>MISSION_CRITICAL</option>
                        <option>SYSTEM_AUDIT</option>
                        <option>COLLABORATION</option>
                        <option>GENERAL BREEFING</option>
                      </select>
                    </div>
                  </div>
                  <div>
                    <label className="text-[#00FF00]/70 text-[10px] uppercase block mb-2 tracking-widest"> ! PAYLOAD (Message)</label>
                    <textarea rows="4" placeholder="Describe your mission, project, or collaboration interest..." className="w-full bg-black/80 border border-[#00FF00]/30 p-4 text-[#00FF00] text-sm focus:border-[#00FF00] outline-none resize-none" required></textarea>
                  </div>
                  
                  <div className="flex gap-4">
                    <button type="submit" className="flex-1 bg-[#00FF00] text-black font-bold py-3 uppercase tracking-widest text-xs hover:bg-white transition-all">Transmit Payload</button>
                    <button type="button" onClick={() => setIsOpen(false)} className="px-6 py-3 border border-red-500 text-red-500 uppercase tracking-widest text-xs font-bold hover:bg-red-500/10">Abort.Comunication</button>
                  </div>
                  

                  <div className="absolute inset-0 z-0 opacity-[0.08] pointer-events-none flex items-center justify-center">
                    <pre className="text-[#00FF00] text-[8px] leading-[3px] font-mono whitespace-pre">
                      {displayedAscii}
                    </pre>
                  </div>
                  <div class="mt-8 border-t border-[#00FF00]/20 pt-6">
                    <div class="flex items-center gap-2 mb-3">
                        <span class="relative flex h-2 w-2">
                            <span class="animate-ping absolute inline-flex h-full w-full rounded-full bg-[#00FF00] opacity-75"></span>
                            <span class="relative inline-flex rounded-full h-2 w-2 bg-[#00FF00]"></span>
                        </span>
                        <span class="text-[#00FF00] font-mono text-[10px] tracking-[0.2em] uppercase">Public_GPG_Registry</span>
                    </div>

                    <div class="bg-[#00FF00]/5 border border-[#00FF00]/20 p-4 rounded-sm group hover:border-[#00FF00]/60 transition-colors">
                        <div class="flex justify-between items-start mb-2">
                            <code class="text-[#00FF00] text-[9px] font-mono">ID: 0x8BCE42FF... (ENCRYPTED COMUNICATION NO PROBLEM)</code>
                            <button class="text-[9px] text-white bg-[#00FF00]/20 px-2 py-1 hover:bg-[#00FF00] hover:text-black transition-all font-mono uppercase">Copy_Key</button>
                        </div>
                        
                        <pre class="text-[#00FF00]/40 text-[7px] leading-tight font-mono overflow-x-hidden whitespace-pre-wrap">
                -----BEGIN PGP PUBLIC KEY BLOCK-----
                Version: GnuPG v2

                mQENBF2... [YOUR_ACTUAL_GPG_KEY_DATA_HERE] ...
                ... [IT LOOKS AMAZING AS DECORATIVE CODE] ...
                -----END PGP PUBLIC KEY BLOCK-----
                        </pre>
                    </div>
                </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}